import React from 'react';
import "./ActivityItem.css";

export default function ActivityItem({firebase, isAuth, activity, client}){
  const {firstName, lastName} = client;
  const {type, amount} = activity;

  if(type === 'payment'){
    return(
      <div className="activityItem payment" >
        <span>{`${firstName} has paid`}</span>
        <span className="amount">{`\ \ $${amount}`}</span>
      </div>
    );
  }else{
    return(
      <div className="activityItem loan" >
        <span>{`${firstName} has borrowed`}</span>
        <span className="amount">{`\ \ $${amount}`}</span>
      </div>
    );
  }
}
