import React from 'react';
import "./ActivityItem.css";

export default function ActivityItem({firebase, isAuth, activity, client}){
  const {firstName, lastName} = client;
  const {type, amount} = activity;

  const renderActivity = () => {
    if(type === 'payment'){
      return <span className="payment">{`has paid $${amount}`}</span>;
    }else{
      return <span className="loan">{`has borrowed $${amount}`}</span>;
    }
  };

  return (
    <div className="activityItem">
      <span className="clientName">{`${firstName}`}</span>
      {renderActivity()}
    </div>
  );
}
