import React, {useState} from 'react';
import './App.css';
import { Link } from "react-router-dom";
import Routes from './Routes';
import * as firebase from "firebase/app";
import "firebase/auth";

const firebaseConfig = require('./firebaseConfig.json');
firebase.initializeApp(firebaseConfig);

function App() {
  const [isAuthenticated, userHasAuthenticated] = useState(false);

  firebase.auth().onAuthStateChanged(function(usr) {
    if (usr) {
      userHasAuthenticated(true);
      console.log('user is logged in');
    } else {
      userHasAuthenticated(false);
      console.log('user has logged out');
    }
  });

  const handleLogout = () => {
    firebase.auth().signOut().then(function() {
      console.log('logout succed');
    }).catch(function(error) {
      console.log('logout failed');
    });
  };


  const renderNavBar = () => {
    if(isAuthenticated){
      return(
        <div className="navbar notLoggedIn">
          <Link to="/signin" className="navItem">Sign In</Link>
          <Link to="/signup" className="navItem">Sign Up</Link>
        </div>
      );
    } else {
      <div></div>
    }
  }

  return (
    <div className="App">
      <div className="navbar">
        {isAuthenticated ? (<Link to="/" className="navItem" onClick={() => handleLogout()}>Log out</Link>) : (
          <>
            <Link to="/signin" className="navItem">Sign In</Link>
            <Link to="/signup" className="navItem">Sign Up</Link>
          </>
        )}
      </div>
      <Routes isAuth={isAuthenticated} firebase={firebase} />
    </div>
  );
}

export default App;
